mapper.select:
SELECT

FROM
learn_video_year_top_list
WHERE
sale_no = #{saleNo}
AND `year` = #{year}
AND duration_unit = #{unit}
order by update_time desc
LIMIT 0,1

SELECT

FROM
learn_video_year_top_list
WHERE
`year` = #{year}
AND duration_unit = #{unit}


        order by ranking ${order}


        order by ranking asc




        LIMIT ${startIndex},${count}


        LIMIT 0,200

SELECT a.id,a.sale_no,a.duration,a.duration_unit,a.ranking, b.name, c.short_name as shortName, d.head_img_url as headImgUrl FROM learn_video_year_top_list a LEFT JOIN salesman b ON a.sale_no = b.sale_no LEFT JOIN dict_branch c ON b.branch_code = c.id LEFT JOIN learn_user_head_img d on d.work_no=b.sale_no WHERE a.`year` = #{year} AND a.duration_unit = #{unit}
order by a.ranking ${order}
order by a.ranking asc

SELECT count(*)
FROM
learn_video_year_top_list
WHERE
`year` = #{year}
AND duration_unit = #{unit}
